return {
	en = {

		["mod_name"] = "Car transport wagons",
		["mod_desc"] = "The increasing production in the automotive industry in the 1950s rendered transportation on simple flatcars economically impractical. This marked the onset of the development of specialized car transport wagons in Germany. Initially created through modifications of other cars, they became increasingly specialized over time to meet the growing demands of the new, larger cars. This evolution began in the economic boom years when car producers sought tailored logistics solutions. Double-decker car transport wagons were introduced during this period. Today, the transportation of luxury cars from Porsche, Mercedes, Audi, and BMW largely occurs incognito in closed car transport wagons. These transportation methods have continually evolved to meet the needs of the automotive industry, now carrying not only vehicles but also parts, both within Germany and internationally.",
		["assetPerson_mod_name"] = "Car transport wagons (Passenger variants)",
		["assetGuter_mod_name"] = "Car transport wagons (Cargo variants)",
		["Beladung"] = "Cargo",
		["assetVariante_1"] = "Empty",
		["assetVariante_2"] = "Variant I",
